import React, { useState } from 'react';
import MainPage from "./Сomponents/mainPages/MainPage";
import RegistrationPage from "./Сomponents/registrationpage/RegistrationPage";
import "./App.css";


function App() {
  const [currentPage, setCurrentPage] = useState("main"); // Изначально установим "main"


   const handlePageChange = (page) => {
     setCurrentPage(page);
   }

  return (
    <div>
      {currentPage === "main" ? (
        <MainPage onRegister={() => handlePageChange("registration")} /> // Передаем функцию для изменения страницы
      ) : (
        <RegistrationPage />
      )}
    </div>
  );
}

export default App;
