import React from "react";
import "./RegistrationPage.css";
import logo from "../../resources/mainpage/logo.png";
import { Link } from "react-router-dom";

const RegistrationPage = ({ onLogin }) => {
  const handleRegistration = () => {
    // Выполняйте логику регистрации, затем вызывайте onLogin
    // ...

    // После успешной регистрации вызываем onLogin
    onRegistration();
  };

  return (
    <div className="registration-page">
      <div className="container h-100">
        <div className="row h-100 align-items-center justify-content-center">
          <div className="col-md-4">
            <div className="registration-form">
              <img className="registration-logo" src={logo} alt="logo" />
              <form>
                <input type="text" placeholder="Username" />
                <input type="password" placeholder="Password" />
                <button className="btn btn-login" onClick={handleRegistration}>
                  LOG IN
                </button>
                <button className="btn btn-link btn-forgot">
                  Forgot your password?
                </button>
              </form>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default RegistrationPage;
