import React, { useState } from "react";
import MainPage from "./Сomponents/mainPage/MainPage";
import RegistrationPage from "./Сomponents/registrationPage/RegistrationPage";
import PDFUploadPage from "./Сomponents/pdfUploadPage/PdfUploadPage"; // Импортируйте страницу для загрузки PDF
import { BrowserRouter as Router, Route, Routes } from "react-router-dom";
import "./App.css";

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  const handleLogin = () => {
    setIsLoggedIn(true);
  };

  return (
    <Router>
      <Routes>
        <Route
          path="/"
          element={
            isLoggedIn ? (
              <PDFUploadPage /> // Показывать страницу загрузки PDF, если пользователь залогинен
            ) : (
              <MainPage onLogin={handleLogin} /> // Показывать главную страницу, если не залогинен
            )
          }
        />
        <Route path="/registration" element={<RegistrationPage onLogin={handleLogin} />} />
      </Routes>
    </Router>
  );
}

export default App;
